mapper.select:
SELECT
  IF((s.play_period < 1 OR s.play_period='없음' OR s.play_period IS null), '0시간',
    IF(s.play_period >= 1 AND s.play_period < 3, '1~3시간',
      IF(s.play_period >= 3 AND s.play_period < 6, '3~6시간',
        IF(s.play_period >= 6 AND s.play_period < 10, '6~10시간',
          IF(s.play_period >= 10, '10시간 이상', '')
        )
      )
    )
  ) AS play_period,
  COUNT(*) AS count_play_period
FROM survey s
GROUP BY
  IF((s.play_period < 1 OR s.play_period='없음' OR s.play_period IS null), '0시간',
    IF(s.play_period >= 1 AND s.play_period < 3, '1~3시간',
      IF(s.play_period >= 3 AND s.play_period < 6, '3~6시간',
        IF(s.play_period >= 6 AND s.play_period < 10, '6~10시간',
          IF(s.play_period >= 10, '10시간 이상', '')
        )
      )
    )
  )
ORDER BY count_play_period DESC

	SELECT
	    IF((s.master = 0 OR s.master IS null OR s.master='없음'), '0회',
	      IF(s.master >= 1 AND s.master <= 2, '1~2회',
	        IF(s.master >= 3 AND s.master <= 4, '3~4회',
	          IF(s.master >= 5, '5회 이상', '')
	        )
	      )
	    ) AS master
    	, COUNT(*) AS count_master
	FROM survey s
	GROUP BY
	    IF((s.master = 0 OR s.master IS null OR s.master='없음'), '0회',
	      IF(s.master >= 1 AND s.master <= 2, '1~2회',
	        IF(s.master >= 3 AND s.master <= 4, '3~4회',
	          IF(s.master >= 5, '5회 이상', '')
	        )
	      )
	    )
