mapper.select:
SELECT basket_num
FROM basket
WHERE id = #{id}

mapper.update:
UPDATE purchase p
JOIN basket b ON b.basket_num = p.basket_num
JOIN subscribe s ON p.sub_num = s.sub_num
SET p.amount = #{updatedAmount}
WHERE b.basket_num = #{basketNum} AND s.price = #{price}

UPDATE purchase p
JOIN basket b ON b.basket_num = p.basket_num
JOIN subscribe s ON p.sub_num = s.sub_num
SET p.amount = #{amount}
WHERE p.sub_num = #{sub_num}
